- (BOOL)isPhoneNumberValid:(NSString *)phoneNumber
{
    for (NSString* prefix in self.areaCodes) {
        if ([phoneNumber hasPrefix:prefix]) {
            return YES;
        }
    }
    
    return NO;
}

